function add_numbers(a, b) {
    return a + b; // Only works with numberic types
}

var result_number = add_numbers(2, 5); // Adds two numbers
console.log(result_number);

function add_strings(a, b) {
    return a + b; // Only works with string types
}

var result_string = add_strings("2", "5"); // Concatenates two characters
console.log(result_string);
